import {Component} from '@angular/core';
import {InuCheckboxGroup} from "inugami-ng/components/inu-checkbox-group";

interface MyFormModel {
  verb: string[];
}

@Component({
  templateUrl: './my-app.view.html',
  styleUrls: ['./my-app.view.scss'],
  imports: [
    InuCheckboxGroup
  ]
})
export class MyAppView {

  formModel = signal<MyFormModel>({
    verb: ['GET', 'PUT']
  });

  myForm = form(this.formModel, (path) => {
    required(path.verb);
    disabled(path.verb);
  });

  verbs = computed<InuSelectItem<string>[]>(() => [
    {value: 'GET', title: 'GET', styleClass: 'verb-get'},
    {value: 'POST', title: 'POST', styleClass: 'verb-post'},
    {value: 'PUT', title: 'PUT', styleClass: 'verb-put'}
  ]);
}



@Component({
  // ...
})
export class MyAppView {
  formModel = signal<MyFormModel>({ verb: [] });

  myForm = form(this.formModel, (path) => {
    required(path.verb);
  });
}



@Component({
  // ...
})
export class MyAppView {
  formModel = signal<MyFormModel>({ verb: [] });

  myForm = form(this.formModel, (path) => {
    disabled(path.verb);
  });
}


export interface InuSelectItem<T>{
  title:string;
  value:T;
  styleClass?:string;
  disabled?:boolean;
  selected?:boolean;
}
