}


import {Component} from '@angular/core';
import {InuButton} from 'inugami-ng/components/inu-button';
import {InuToastServices} from 'inugami-ng/components/inu-toast';

@Component({
  templateUrl: './my-app.view.html',
  styleUrls: ['./my-app.view.scss'],
  imports: [
    InuButton
  ]
})
export class MyAppView {

  toastServices = inject(InuToastServices);

  protected addMessage() {
    this.toastServices.addMessage({
      title: 'Hello',
      message: 'some message',
      level: "info",
      icon:'idea'
    });
  }
}


this.toastServices.addMessage({
  title: 'Hello',
  message: 'some message',
  level: "info",
  icon:'idea'
});


export interface ToastMessage {
  title?: string;
  message?: string;
  level?:'debug'|'success'|'info'|'warn'|'error';
  icon?: string;
  delay?:number;
}
